package com.example.expensetracker;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.EditText;import com.example.expensetracker.controler.ExpenseTracker;public class AddIncomeActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_add_income);    }    public void addIncomeButtonClick(View view){        EditText editText_income = findViewById(R.id.incomeInput);        EditText editText_descriptionInput = findViewById(R.id.incomeDescriptionInput);        float value = Float.parseFloat(editText_income.getText().toString());        String description = editText_descriptionInput.getText().toString();        ExpenseTracker.getInstance().addIncome(value, description);        finish();    }}